import {settings} from "../../config/config";
import {useAuth} from "../../services/useAuth";
import useAxios from "../../services/useAxios";
import {NavLink, Outlet, useLocation} from "react-router-dom";
import {useState} from "react";
import "./ingredient.css";
import Ingredient from "./ingredient";
import Pagination from "./pagination";

const Ingredients = () => {


    const {user} = useAuth();
    const {pathname} = useLocation();
    const [subHeading, setSubHeading] = useState("All Ingredients");
    const url = settings.baseApiUrl + "/ingredients";

    //declare the data fetching function
    const {
        error,
        isLoading,
        data: ingredients
    } = useAxios(url, "GET", {Authorization: "Bearer " + user.jwt});

    
    useEffect(() => {
        setSubHeading("All Ingredients");
    }, [pathname]);

    return (
        <>
            {/* {error && <div>{error}</div>} */}
            {isLoading &&
                <div className="image-loading">
                    Please wait while data is being loaded
                    <img src={require(`../loading.gif`)} alt="Loading ......"/>
                </div>}
            {ingredients && 
            <>
                {setSubHeading(ingredients.name)}
                <div className="menuitem-details">
                    {/* <div className="menuitem-name">{menuitem.name}</div>*/}
                    <div className="menuitem-info">
                        <div><strong>ID</strong>: {ingredients.itemID}</div>
                        <div><strong>Item Name</strong>: {ingredients.name}</div>
                        <div><strong>Description</strong>: {ingredients.description}</div>
                        <div><strong>Price</strong>: {ingredients.price}</div>
                        {/* <div><strong>Ingredients</strong>:
                            <Link to={`menuitemingredients/${menuitem.itemID}`}> Click here to view ingredients</Link>
                        </div> */}
                    </div>
                    {/* <div className="menuitem-photo">
                        <img src={menuitem.image} alt={menuitem.name} id={menuitem.itemID}/>
                    </div> */}
                </div>
                <div className="menuitem-classes">
                    <Outlet/>
                </div>
            </>}
        </>
    );
};

export default Ingredients;
