/*
Name: your name
Date: today's date
File: students.js
Description: create a component to list all students.
 */
import {useEffect, useState} from 'react';
import UseFetch from "../../services/useFetch";
import {useNavigate} from "react-router-dom";
import {useAuth} from "../../services/useAuth";
import JSONPretty from 'react-json-pretty';
import EditStudent from "./editStudent";
import CreateStudent from "./createStudent";
import DeleteStudent from "./deleteStudent";
import "./student.css";

const Students = () => {
    const {error, isLoading, data: students, getAll, search} = UseFetch();
    const [subHeading, setSubHeading] = useState("All Students");
    const navigate = useNavigate();
    const [activeStudent, setActiveStudent] = useState("");  //the student being edited
    const [showEditModal, setShowEditModal] = useState(false);
    const [showDeleteModal, setShowDeleteModal] = useState(false);
    const [reload, setReload] = useState(false);
    const [showCreateModal, setShowCreateModal] = useState(false);

    //user authorization
    const {user} = useAuth();
    const disabled = (user.role !== 1);

    useEffect(() => {
        getAll();
    }, [reload]);

    const handleSearch = (e) => {
        e.preventDefault();
        const term = document.getElementById("student-search-term").value;
        if(term == '')
            setSubHeading("All Students");
        else if(isNaN(term))
            setSubHeading("Students containing '" + term + "'");
        else if(!isNaN(term))
            setSubHeading("Students whose GPA is >= " + term);

        search(term);
    }

    const clearSearchBox = (e) => {
        e.preventDefault();
        document.getElementById("student-search-term").value = "";
        search("");
    }
	
    const handleEdit = (e) => {
		if(disabled) return;
		
        //retrieve student data and pass it to the update page
        let student = {};
        ["id", "name", "email", "major", "gpa"].forEach(function(key)
        {
            student[key] = document.getElementById(`student-${key}-` + e.target.id).innerText;
        })
        setActiveStudent(student);
        navigate("/students/" + e.target.id);
        setShowEditModal(true);
        setSubHeading("Edit Student");
    }

    const handleCreate = (e) => {
		if(disabled) return;
		
        setShowCreateModal(true);
        setSubHeading("Create Student");
    }

    const handleDelete = (e) => {
		if(disabled) return;
		
        let student = {};
        ["id", "name", "email", "major", "gpa"].forEach(function (key) {
            student[key] = document.getElementById(`student-${key}-` + e.target.id).innerText;
        })
        setActiveStudent(student);
        setSubHeading("Delete Student");
        navigate("/students/" + e.target.id);
        setShowDeleteModal(true);
    }

    return (
        <>
            <div className="main-heading">
                <div className="container">Student</div>
            </div>
            <div className="sub-heading">
                <div className="container">{subHeading}</div>
            </div>
            <div className="main-content container">
                {error && <JSONPretty data={error}></JSONPretty>}
                {isLoading &&
                <div className="image-loading">
                    Please wait while data is being loaded
                    <img src={require(`../loading.gif`)} alt="Loading ......"/>
                </div>}
                {students &&
                <div className="student-container">
                    <form style={{textAlign: "right", marginBottom: "3px"}} onSubmit={handleSearch} >
                        <input id="student-search-term" placeholder="Enter search terms"/>
                        <button type="submit" className="button-light" style={{marginLeft: "5px"}}>Search</button>
						<button className="button-light" style={{marginLeft: "5px"}} onClick={clearSearchBox}>Clear</button>
                    </form>
					
                    <div className="student-row student-row-header">
                        <div className="student-info">
                            <div className="student-id">Student ID</div>
                            <div className="student-name">Name</div>
                            <div className="student-email">Email</div>
                            <div className="student-major">Major</div>
                            <div className="student-gpa">GPA</div>
                        </div>
                        <div className="student-buttons" style={{textAlign: "center"}}>Actions</div>
                    </div>
                    {students.map((student) => (
                        <div key={student.id} className="student-row">
                            <div className="student-info">
                                <div id={"student-id-" + student.id} className="student-id">{student.id}</div>
                                <div id={"student-name-" + student.id} className="student-name">{student.name}</div>
                                <div id={"student-email-" + student.id} className="student-email">{student.email}</div>
                                <div id={"student-major-" + student.id} className="student-major">{student.major}</div>
                                <div id={"student-gpa-" + student.id} className="student-gpa">{student.gpa}</div>
                            </div>
                            <div className="student-buttons">
                                <button className="button-light" id={student.id} disabled={disabled} onClick={handleEdit}>Edit</button>
                                <button className="button-light" id={student.id} disabled={disabled} onClick={handleDelete}>Delete</button>
                            </div>
                        </div>
                    ))}
                </div>}
                {showEditModal &&
                <EditStudent showModal={showEditModal} setShowModal={setShowEditModal} data={activeStudent} reload={reload} setReload={setReload} setSubHeading={setSubHeading}/>}
                {showDeleteModal &&
                <DeleteStudent showModal={showDeleteModal} setShowModal={setShowDeleteModal} data={activeStudent} reload={reload} setReload={setReload} setSubHeading={setSubHeading}/>}
                {showCreateModal &&
                <CreateStudent showModal={showCreateModal} setShowModal={setShowCreateModal} reload={reload} setReload={setReload} setSubHeading={setSubHeading}/>}
                <div><button className="button-create" disabled={disabled} onClick={handleCreate}>Create Student</button></div>
            </div>
        </>
    );
};

export default Students;
